/**
 * Enum of all input types (for INPUT, BUTTON, SELECT and TEXTAREA elements)
 * specified by the W3C HTML4.01 and HTML5 specifications.
 */
export type InputType = string;
export namespace InputType {
    export const BUTTON: string;
    export const CHECKBOX: string;
    export const COLOR: string;
    export const DATE: string;
    export const DATETIME: string;
    export const DATETIME_LOCAL: string;
    export const EMAIL: string;
    export const FILE: string;
    export const HIDDEN: string;
    export const IMAGE: string;
    export const MENU: string;
    export const MONTH: string;
    export const NUMBER: string;
    export const PASSWORD: string;
    export const RADIO: string;
    export const RANGE: string;
    export const RESET: string;
    export const SEARCH: string;
    export const SELECT_MULTIPLE: string;
    export const SELECT_ONE: string;
    export const SUBMIT: string;
    export const TEL: string;
    export const TEXT: string;
    export const TEXTAREA: string;
    export const TIME: string;
    export const URL: string;
    export const WEEK: string;
}
